mapper.select:
select
from server where 1=1
and server_id = #{server_id,jdbcType=INTEGER}

mapper.insert:
insert into server (server_id, server_name, server_type, 
  server_ip, server_port, server_desc, 
  XMPPAccount, XMPPPwd, module_media, 
  module_control, module_store, module_record
  )
values (#{server_id,jdbcType=INTEGER}, #{server_name,jdbcType=VARCHAR}, #{server_type,jdbcType=VARCHAR}, 
  #{server_ip,jdbcType=VARCHAR}, #{server_port,jdbcType=INTEGER}, #{server_desc,jdbcType=VARCHAR}, 
  #{XMPPAccount,jdbcType=VARCHAR}, #{XMPPPwd,jdbcType=VARCHAR}, #{module_media,jdbcType=INTEGER}, 
  #{module_control,jdbcType=INTEGER}, #{module_store,jdbcType=INTEGER}, #{module_record,jdbcType=INTEGER}
  )
